        AND CASE WHEN core.verification_statuses.verification_status_id > 0 THEN 'true' WHEN verification_status_id <= 0 AND NOT verification_status_id = -1 THEN 'false' ELSE 'withdrawn' END LIKE '%' || lower(_authorized) || '%'
