DROP VIEW IF EXISTS core.item_scrud_view;

CREATE VIEW core.item_scrud_view
AS
SELECT 
        item_id,
        item_code,
        item_name,
        item_group_code || ' (' || item_group_name || ')' AS item_group,
        maintain_stock,
        brand_code || ' (' || brand_name || ')' AS brand,
